    private void btnRegisterActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRegisterActionPerformed
        var name = txtName.getText();
        var email = txtEmail.getText();
        var cellphone = txtCellphone.getText();
        var password = new String(txtPassword.getPassword());
        var confirmPassword = new String(txtConfirmPassword.getPassword());
        
        if(!password.equals(confirmPassword)){
            System.out.println("Las contrase√±as no coinciden");
        }
        clientController.registerUser(name, email, password, cellphone);
        FrmLogin frm = new FrmLogin();
        frm.setVisible(true);
        
    }//GEN-LAST:event_btnRegisterActionPerformed


